cdata.botryllid <- summaryBy(botryllid ~ Treatment + Invasives + CO2.Treatment + Week, data=invasion.exp.data, FUN=function(x) { c(mean = mean(x), sd = sd(x), length=length(x)) } )
names(cdata.botryllid)[names(cdata.botryllid)=="botryllid.length"]<-"N"
cdata.botryllid$botryllid.se<-cdata.botryllid$botryllid.sd/sqrt(cdata.botryllid$N)
head(cdata.botryllid)



plot.botryllid<-ggplot(cdata.botryllid, aes(x=Week, y=botryllid.mean, shape=Treatment)) + geom_errorbar(aes(ymin=botryllid.mean-botryllid.se, ymax=botryllid.mean+botryllid.se), width=0.25) + geom_line(aes(linetype=CO2.Treatment), size=1) + geom_point(aes(shape=Treatment),size=5, col="#666666") +scale_shape_manual(values=c(1,19,2,17), name="Treatment: CO2, Invasives")+scale_linetype_manual(values=c("solid", "dashed"))+guides(lty=FALSE) 
plot.botryllid<- plot.botryllid + labs(x = "Time (weeks)", y="% cover Botryllus")+ theme_bw()+ theme(text = element_text(size=16), axis.text = element_text(size=16)) + theme(axis.title.y = element_text(angle=90))+xlim(0,16)
plot.botryllid<-plot.botryllid + theme(legend.text = element_text(colour="black", size = 10))+theme(legend.title = element_text(colour="black", size=12), legend.position=c(.17,.80))+theme(panel.grid.major = element_blank(), panel.grid.minor=element_blank(), axis.line=element_line(size=0.25), axis.ticks.length=unit(-0.25, "cm") )+ theme(legend.key = element_blank())+theme(legend.key.size = unit(0.01, "cm"))
